                            (b5.b || '') +
                            '}}_{\\hphantom{' +
                            (b5.p || '') +
                            '}}';
                    res += '{\\vphantom{X}}';
                    res +=
                        '^{\\smash[t]{\\vphantom{2}}\\mathllap{' +
                            (b5.b || '') +
                            '}}';
                    res +=
                        '_{\\vphantom{2}\\mathllap{\\smash[t]{' +
                            (b5.p || '') +
                            '}}}';
